css:
@font-face {
    font-family: daxline;
    src: url('/assets/mietrechtspraxis/fonts/daxline_w01_regular/bbfcf1aa-d115-47af-9a17-20cd6809531d.woff');
}

@font-face {
    font-family: daxlineBold;
    src: url('/assets/mietrechtspraxis/fonts/daxline_w01_bold/fe2da1f8-8be5-44dd-ac81-edc4fc5564d6.woff');
}

.print-format {
  font-family: daxline;
  font-size: 10pt;
  line-height: 13.5pt;
}

html:
{% if doc.versandkarten %}
    {% for versandkarte in doc.versandkarten %}
        <!-- load contact -->
        {% set contact = frappe.get_doc("Contact", versandkarte.recipient_contact) %}
        <!-- load address -->
        {% set address = frappe.get_doc("Address", versandkarte.recipient_address) %}
        <!-- load customer -->
        {% set customer = frappe.get_doc("Customer", contact.links[0].link_name) %}

        <div style="float: left; width: 45%; min-height: 30mm; border: 1px solid red;">
            Bitte überprüfen Sie die Richtigkeit Ihrer Adresse:<br><br>
            Abo.-Nummer: {{ versandkarte.abo }}<br>
            Anzahl Hefte: {{ versandkarte.anz }}
        </div>
        <div style="float: right; width: 45%; min-height: 30mm; border: 1px solid red;">
            <img src="/assets/mietrechtspraxis/img/mietrechtspraxis_mp.png">
        </div>
        <div style="clear: both;"></div>
        <div style="float: left; width: 45%; min-height: 100mm; padding-top: 45px; border: 1px solid red; background-image: url('/assets/mietrechtspraxis/img/PP-AZ101-46039.png'); background-size: 300px 100px; background-repeat: no-repeat;" >
            {% if customer.customer_type == 'Company' %}
                {{ customer.customer_name }}<br>
                {% if customer.customer_addition %}
                    {{ customer.customer_addition }}<br>
                {% endif %}
                {% if customer.customer_name | replace(" ", "") != contact.first_name | replace(" ", "") + contact.last_name | replace(" ", "") %}
                    {{ contact.first_name }} {{ contact.last_name }}<br>
                {% endif %}
            {% else %}
                {{ contact.first_name }} {{ contact.last_name }}<br>
            {% endif %}
            {% if address.address_line2 %}
                {{ address.address_line2 }}<br>
            {% endif %}
            {{ address.address_line1 }}{% if address.postfach %} {{ address.postfach_nummer }}{% endif %}<br>
            {{ address.pincode }} {{ address.city }}
        </div>

        <div style="float: right; width: 45%; min-height: 100mm; border: 1px solid red;">
            <h2>{{ _("Holen Sie sich unsere Kurse in Ihr Unternehmen - à la carte!") }}</h2>
            Stossen Sie und Ihre Kolleginnen und Kollegen<br>
            immer wieder auf die gleichen mietrechtlichen<br>
            Fragen? Gibt es in Ihrem Unternehmen gleich<br>
            mehrere Personen, die Bedarf an einem unserer<br>
            Kurse haben? Oder wünschen Sie einen Kurs, der<br>
            thematisch auf Ihre ganz besonderen Wünsche<br>
            ausgerichtet ist?<br><br>
            Dann melden Sie sich direkt bei uns unter<br>
            <u>mietrecht@mietrecht.ch</u> oder telefonisch unter<br>
            043 243 40 50. Gemeinsam mit Ihnen können wir<br>
            Ihren "Mietrechts-Kurs à la carte"<br>
            zusammenstellen!
        </div>
        <div style="clear: both; {{ loop.cycle('margin-bottom: 60mm;', '') }}  border: 1px solid red;">Adressänderungen bitte an mietrecht@mietrecht.ch</div>
        {{ loop.cycle('', '<div class="page-break"></div>') }}
    {% endfor %}
{% endif %}
